$(document).ready(function(){if(screen.width>780){var swiper=new Swiper(".action_slider_swiper",{slidesPerView:4,spaceBetween:10,freeMode:true,autoplay:{delay:5e3},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}})}if(screen.width<780){$.each($(".action_img_class"),function(){$(this).attr("src",$(this).data("mob"))})}else{$.each($(".action_img_class"),function(){$(this).attr("src",$(this).data("desc"))})}});